import React from "react";

class QuoteBox extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      currentQuote: "",
      currentAuthor: "",
    };
    this.handleNewQuoteClick = this.handleNewQuoteClick.bind(this);
    this.handleAuthorClick = this.handleAuthorClick.bind(this);
  }

  componentDidMount() {
    // Choose a random quote and author when component mounts
    this.chooseRandomQuote();
  }

  componentDidUpdate(prevProps) {
    // If search query or language changes, choose a new random quote
    if (
        this.props.searchQuery !== prevProps.searchQuery ||
        this.props.language !== prevProps.language
    ) {
      this.chooseRandomQuote();
    }
  }

  chooseRandomQuote() {
    const {quotes, authors, searchQuery} = this.props;
    let filteredQuotes = quotes;
    let filteredAuthors = authors;

    // Filter quotes and authors by search query
    if (searchQuery) {
      filteredQuotes = quotes.filter((quote) =>
          quote.toLowerCase().includes(searchQuery.toLowerCase())
      );
      filteredAuthors = authors.filter((author, i) =>
          quotes[i].toLowerCase().includes(searchQuery.toLowerCase())
      );
    }

    // Choose a random quote from the filtered quotes
    const randomIndex = Math.floor(Math.random() * filteredQuotes.length);
    const currentQuote = filteredQuotes[randomIndex];
    const currentAuthor = filteredAuthors[randomIndex];

    // Update state with the new quote and author
    this.setState({
      currentQuote,
      currentAuthor,
    });
  }

  handleNewQuoteClick() {
    // Choose a new quote with the same author
    const {authors, searchQuery} = this.props;
    const {currentAuthor} = this.state;
    let filteredAuthors = authors;

    // Filter authors by search query
    if (searchQuery) {
      filteredAuthors = authors.filter((author) =>
          author.toLowerCase().includes(searchQuery.toLowerCase())
      );
    }

    // Choose a new quote with the same author
    const quotesWithAuthor = this.props.quotes.filter(
        (quote, i) => authors[i] === currentAuthor
    );
    const randomIndex = Math.floor(Math.random() * quotesWithAuthor.length);
    const currentQuote = quotesWithAuthor[randomIndex];

    // Update state with the new quote
    this.setState({
      currentQuote,
    });
  }

  handleAuthorClick(author) {
    // Choose a new quote from the selected author
    const {quotes, authors} = this.props;
    const quotesByAuthor = quotes.filter(
        (quote, i) => authors[i] === author
    );
    const randomIndex = Math.floor(Math.random() * quotesByAuthor.length);
    const currentQuote = quotesByAuthor[randomIndex];

    // Update state with the new quote and author
    this.setState({
      currentQuote,
      currentAuthor: author,
    });
  }

  render() {
    const {
      isLoading,
      onLanguageChange,
      onFavorite,
      onSearch,
      searchPlaceholder,
      onBackgroundColorChange,
      onFontSizeChange,
      newQuoteBtn,
      tweetBtn,
      tumblrBtn,
      facebookBtn,
      favoriteBtn,
      favorites,
      language,
    } = this.props;

    const {currentQuote, currentAuthor} = this.state;

    return (
        <div
            className="QuoteBox"
            style={{
              backgroundColor: this.props.backgroundColor,
              fontSize: this.props.fontSize,
            }}
        >
          <div className="QuoteBox-quote">
            {isLoading ? (
                <p>Loading...</p>
            ) : (
                <p>
                  <i className="fas fa-quote-left"></i>
                  {currentQuote}
                </p>
            )}
          </div>
          <div className="QuoteBox-author">
            {isLoading ? (
                <p>Loading...</p>
            ) : (
                <p>
                  - <span onClick={() => this.handleAuthorClick(currentAuthor)}>{currentAuthor}</span>
                </p>
            )}
          </div>
          <div className="QuoteBox-btns">
            <button onClick={this.handleNewQuoteClick}>{newQuoteBtn}</button>
            <button onClick={() => onLanguageChange(language === "en" ? "es" : "en")}>
              {language === "en" ? "EspaÃ±ol" : "English"}
            </button>
            <button onClick={() => onFavorite(currentQuote, currentAuthor)}>
              {favorites.includes(currentQuote) ? "Remove from Favorites" : favoriteBtn}
            </button>
            <button onClick={() => onSearch("")}>Clear Search</button>
            <button onClick={() => onBackgroundColorChange()}>Change Background Color</button>
            <button onClick={() => onFontSizeChange()}>Change Font Size</button>
          </div>
          <div className="QuoteBox-share">
            <a
                href={`https://twitter.com/intent/tweet?text=${currentQuote} - ${currentAuthor}`}
                target="_blank"
                rel="noreferrer"
            >
              <i className="fab fa-twitter"></i>
            </a>
            <a
                href={`https://www.tumblr.com/widgets/share/tool?posttype=quote&tags=quotes,freecodecamp&caption=${currentAuthor}&content=${currentQuote}&canonicalUrl=https%3A%2F%2Fwww.tumblr.com%2Fbuttons&shareSource=tumblr_share_button`}
                target="_blank"
                rel="noreferrer"
            >
              <i className="fab fa-tumblr"></i>
            </a>
            <a
                href={`https://www.facebook.com/sharer/sharer.php?u=https%3A%2F%2Fwww.facebook.com%2F&quote=${currentQuote} - ${currentAuthor}`}
                target="_blank"
                rel="noreferrer"
            >
              <i className="fab fa-facebook-f"></i>
            </a>
          </div>
        </div>
    );
  }
}

export default QuoteBox;
