import React from "react";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faQuoteLeft } from '@fortawesome/free-solid-svg-icons';
import { faFacebook, faTumblr, faTwitter } from "@fortawesome/free-brands-svg-icons";
import languages from "../languages";

class QuoteBox extends React.Component {
    constructor(props) {
        super(props);
        // Initialize state with default values
        this.state = {
            favoriteQuotes: [], // an array to store favorite quotes
            searchValue: "", // a string to store search input value
            fontSize: 16, // set default font size to 16
            bgColor: "#f5f5f5", // set default background color to light grey
        };
        // Bind methods to this
        this.handleFavoriteClick = this.handleFavoriteClick.bind(this);
        this.handleSearchChange = this.handleSearchChange.bind(this);
        this.handleFontSizeChange = this.handleFontSizeChange.bind(this);
        this.handleBgColorChange = this.handleBgColorChange.bind(this);
    }

    handleFavoriteClick() {
        // Add current quote to favorite quotes array
        this.setState(prevState => ({
            favoriteQuotes: [...prevState.favoriteQuotes, {quote: this.props.quote, author: this.props.author}]
        }));
    }

    handleSearchChange(e) {
        // Update search input value
        this.setState({
            searchValue: e.target.value,
        });
    }

    handleFontSizeChange(e) {
        // Update font size
        this.setState({
            fontSize: parseInt(e.target.value),
        });
    }

    handleBgColorChange(e) {
        // Update background color
        this.setState({
            bgColor: e.target.value,
        });
    }

    render() {
        const { searchPlaceholder} = languages[this.props.language];

        // Filter quotes based on search input value
        const filteredQuotes = this.props.quotes.filter((quote) => {
            return (
                quote.author.toLowerCase().includes(this.state.searchValue.toLowerCase()) ||
                quote.content.toLowerCase().includes(this.state.searchValue.toLowerCase())
            );
        });

        // Get a random quote from filtered quotes
        const randomQuote = filteredQuotes[Math.floor(Math.random() * filteredQuotes.length)];

        // Set quote and author to default values if random quote is not available
        const quote = randomQuote ? randomQuote.content : this.props.quote;
        const author = randomQuote ? randomQuote.author : this.props.author;

        // Set style for QuoteBox
        const style = {
            fontSize: this.state.fontSize + "px",
            backgroundColor: this.state.bgColor,
        };

        return (
            <div className="quote-card" id="quote-card" style={style}>
                <div className="quote-text">
                    <FontAwesomeIcon icon={faQuoteLeft}/>
                    <span id="text">{quote}</span>
                </div>
                <div className="quote-author">
                    <span id="author">- {author}</span>
                </div>
                <div className="button-container">
                    <button
                        className="button"
                        id="new-quote"
                        onClick={this.props.getNewQuote}
                    >
                        {newQuoteBtn}
                    </button>
                    <button
                        className="button"
                        id="favorite-quote"
                        onClick={this.handleFavoriteClick}
                    >
                        Add to favorites
                    </button>
                    <a
                        className="button"
                        id="tweet-quote"
                        href={`https://twitter.com/intent/tweet?text=${quote} - ${author}`}
                        target="_blank"
                        rel="noopener noreferrer"
                    >
                        <FontAwesomeIcon icon={faTwitter}/>
                    </a>
                    <a
                        className="button"
                        id="tumblr-quote"
                        href={`https://www.tumblr.com/widgets/share/tool?posttype=quote&tags=quotes,freecodecamp&caption=${author}&content=${quote}&canonicalUrl=https%3A%2F%2Fwww.tumblr.com%2Fbuttons&shareSource=tumblr_share_button`}
                        target="_blank"
                        rel="noopener noreferrer"
                    >
                        <FontAwesomeIcon icon={faTumblr}/>
                    </a>
                    <a
                        className="button"
                        id="facebook-quote"
                        href={`https://www.facebook.com/sharer/sharer.php?u=https%3A%2F%2Fwww.facebook.com%2F&quote=${quote} - ${author}`}
                        target="_blank"
                        rel="noopener noreferrer"
                    >
                        <FontAwesomeIcon icon={faFacebook}/>
                    </a>
                </div>
                <div className="search-container">
                    <input
                        type="text"
                        className="search-input"
                        id="search-input"
                        placeholder={searchPlaceholder}
                        onChange={this.handleSearchChange}
                    />
                </div>
                <div className="font-size-container">
                    <label htmlFor="font-size">Font size</label>
                    <input
                        type="range"
                        className="font-size-input"
                        id="font-size"
                        min="10"
                        max="30"
                        value={this.state.fontSize}
                        onChange={this.handleFontSizeChange}
                    />
                </div>
                <div className="bg-color-container">
                    <label htmlFor="bg-color">Background color</label>
                    <input
                        type="color"
                        className="bg-color-input"
                        id="bg-color"
                        value={this.state.bgColor}
                        onChange={this.handleBgColorChange}
                    />
                </div>
            </div>
        );
    }
}

export default QuoteBox;
