import React from "react";

function QuoteBox(props) {
  const quoteBox = document.getElementById("quote-box");

  document.getElementById("new-quote").addEventListener("click", function() {
    if (quoteBox.classList.contains("folded")) {
      quoteBox.classList.remove("folded");
      quoteBox.classList.add("unfolded");
    } else {
      quoteBox.classList.remove("unfolded");
      quoteBox.classList.add("folded");
    }
  });

  return (
    <div className="foldable-container">
      <div className="foldable folded">
        <div className="quote-text">
          <i className="fa fa-quote-left"> </i>
          <span id="text">{props.quote}</span>
        </div>
        <div className="quote-author">
          <span id="author">- {props.author}</span>
        </div>
      </div>
      <div className="foldable unfolded">
        <div className="quote-text">
          <i className="fa fa-quote-left"> </i>
          <span id="text">{props.quote}</span>
        </div>
        <div className="quote-author">
          <span id="author">- {props.author}</span>
        </div>
        <div className="button-container">
          <button
            className="button"
            id="new-quote"
            onClick={props.getNewQuote}
          >
              New Quote
          </button>
          <a
            className="button"
            id="tweet-quote"
            href={`https://twitter.com/intent/tweet?text=${props.quote} - ${props.author}`}
            target="_blank"
            rel="noopener noreferrer"
          >
            <i className="fab fa-twitter"></i>
          </a>
            <a
              className="button"
              id="tumblr-quote"
              href={`https://www.tumblr.com/widgets/share/tool?posttype=quote&tags=quotes,freecodecamp&caption=${props.author}&content=${props.quote}&canonicalUrl=https%3A%2F%2Fwww.tumblr.com%2Fbuttons&shareSource=tumblr_share_button`}
              target="_blank"
              rel="noopener noreferrer"
              >
              <i className="fab fa-tumblr"></i>
              </a>
            <a
              className="button"
              id="facebook-quote"
              href={`https://www.facebook.com/sharer/sharer.php?u=https%3A%2F%2Fwww.facebook.com%2F&quote=${props.quote} - ${props.author}`}
              target="_blank"
              rel="noopener noreferrer"
              >
              <i className="fab fa-facebook"></i>
              </a>
        </div>
      </div>
    </div>
  );
}

export default QuoteBox;
