import React from "react";
import QuoteBox from "./components/QuoteBox";
import "./App.css";
import languages from "./languages";

class App extends React.Component {
  constructor(props) {
    super(props);
    // Initialize state with default values
    this.state = {
      quotes: [], // initialize quotes array
      author: [],
      isLoading: false,
      language: "en",
      favorites: [],
      searchQuery: "",
      backgroundColor: "#fff",
      fontSize: "16px",
    };

    // Bind methods to this
    this.getNewQuote = this.getNewQuote.bind(this);
    this.handleLanguageChange = this.handleLanguageChange.bind(this);
    this.handleFavorite = this.handleFavorite.bind(this);
    this.handleSearch = this.handleSearch.bind(this);
    this.handleBackgroundColorChange = this.handleBackgroundColorChange.bind(this);
    this.handleFontSizeChange = this.handleFontSizeChange.bind(this);
  }

  componentDidMount() {
    // Get a new quote when component mounts
    this.getNewQuote();
  }

  getNewQuote() {
  // Set loading state to true
  this.setState({
    isLoading: true,
  });
  // Fetch a collection of quotes from API
  fetch("https://api.quotable.io/quotes")
    .then((response) => response.json())
    .then((data) => {
      // Update state with new quote data
      this.setState({
        quotes: data.results.map((quote) => quote.content),
        authors: data.results.map((quote) => quote.author),
        isLoading: false,
      });
    })
    .catch((error) => {
      // Handle error
      console.error(error);
    });
}


  handleLanguageChange(e) {
    // Update state with new language
    this.setState({
      language: e.target.value,
    });
  }

  handleFavorite() {
    // Add current quote to favorites array
    const {quote, author} = this.state;
    const favorite = {quote, author};
    const favorites = [...this.state.favorites, favorite];
    this.setState({
      favorites,
    });
  }

  handleSearch(e) {
    // Update search query
    this.setState({
      searchQuery: e.target.value,
    });
  }

  handleBackgroundColorChange(e) {
    // Update background color
    this.setState({
      backgroundColor: e.target.value,
    });
  }

  handleFontSizeChange(e) {
    // Update font size
    this.setState({
      fontSize: e.target.value,
    });
  }
  render() {
    const {
      appTitle,
      newQuoteBtn,
      tweetBtn,
      tumblrBtn,
      facebookBtn,
      searchPlaceholder,
      favoriteBtn
    } = languages[this.state.language];
    return (
        <div className="App" style={{backgroundColor: this.state.fontSize}}>
          <header className="App-header">
            <h1>{appTitle}</h1>
          </header>
          <QuoteBox
              quote={this.state.quotes}
              author={this.state.authors}
              isLoading={this.state.isLoading}
              onNewQuote={this.getNewQuote}
              onLanguageChange={this.handleLanguageChange}
              onFavorite={this.handleFavorite}
              onSearch={this.handleSearch}
              searchPlaceholder={searchPlaceholder}
              onBackgroundColorChange={this.handleBackgroundColorChange}
              onFontSizeChange={this.handleFontSizeChange}
              newQuoteBtn={newQuoteBtn}
              tweetBtn={tweetBtn}
              tumblrBtn={tumblrBtn}
              facebookBtn={facebookBtn}
              favoriteBtn={favoriteBtn}
              favorites={this.state.favorites}
              searchQuery={this.state.searchQuery}
              language={this.state.language}
          />
        </div>
    );
  }
}

export default App;
