import React from "react";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faQuoteLeft } from '@fortawesome/free-solid-svg-icons';
import { faFacebook, faTumblr, faTwitter } from "@fortawesome/free-brands-svg-icons";
import languages from "../languages";

class QuoteBox extends React.Component {
  constructor(props) {
    super(props);
    // Initialize state with default values
    this.state = {
      favoriteQuotes: [], // an array to store favorite quotes
      searchValue: "", // a string to store search input value
      fontSize: 16, // set default font size to 16
      bgColor: "#f5f5f5", // set default background color to light grey
      currentQuote: null, // the currently displayed quote
    };
    // Bind methods to this
    this.handleFavoriteClick = this.handleFavoriteClick.bind(this);
    this.handleSearchChange = this.handleSearchChange.bind(this);
    this.handleFontSizeChange = this.handleFontSizeChange.bind(this);
    this.handleBgColorChange = this.handleBgColorChange.bind(this);
    this.handleNewQuoteClick = this.handleNewQuoteClick.bind(this);
  }

  componentDidMount() {
    // When the component mounts, select a random quote
    this.selectRandomQuote();
  }

  selectRandomQuote() {
    const { quotes } = this.props;
    if (quotes && quotes.length > 0) {
      const randomQuote = quotes[Math.floor(Math.random() * quotes.length)];
      this.setState({ currentQuote: randomQuote });
    }
  }

  handleFavoriteClick() {
    // Add current quote to favorite quotes array
    const { currentQuote, favoriteQuotes } = this.state;
    this.setState({
      favoriteQuotes: [...favoriteQuotes, currentQuote],
    });
  }

  handleSearchChange(e) {
    // Update search input value
    this.setState({
      searchValue: e.target.value,
    });
  }

  handleFontSizeChange(e) {
    // Update font size
    this.setState({
      fontSize: parseInt(e.target.value),
    });
  }

  handleBgColorChange(e) {
    // Update background color
    this.setState({
      bgColor: e.target.value,
    });
  }

  handleNewQuoteClick() {
    // Select a new random quote
    this.selectRandomQuote();
  }

  render() {
    const { currentQuote, favoriteQuotes, searchValue, fontSize, bgColor } = this.state;
    const { newQuoteBtn, tweetBtn, tumblrBtn, facebookBtn, searchPlaceholder } = languages[this.props.language];

    // Set style for QuoteBox
    const style = {
      fontSize: `${fontSize}px`,
      backgroundColor: bgColor,
    };

    return (
      <div className="quote-card" id="quote-card" style={style}>
        {currentQuote && (
          <>
            <div className="quote-text">
              <FontAwesomeIcon icon={faQuoteLeft} />
              <span id="text">{currentQuote.content}</span>
            </div>
            <div className="quote-author">
              <span id="author">- {currentQuote.author}</span>
            </div>
            <div className="button-container">
              <button className="button" id="new-quote" onClick={this.handleNewQuoteClick}>
                {newQuoteBtn}
                </button>
                <button
                    className="button"
                    id="favorite-quote"
                    onClick={this.handleFavoriteClick}
                >
                    Add to favorites
                </button>
                <a
                    className="button"
                    id="tweet-quote"
                    href={`https://twitter.com/intent/tweet?text=${currentQuote.content} - ${currentQuote.author}`}
                    target="_blank"
                    rel="noopener noreferrer"
                >
                    <FontAwesomeIcon icon={faTwitter}/>
                    {tweetBtn}
                </a>
                <a
                    className="button"
                    id="tumblr-quote"
                    href={`https://www.tumblr.com/widgets/share/tool?posttype=quote&tags=quotes,freecodecamp&caption=${currentQuote.author}&content=${currentQuote.content}&canonicalUrl=https%3A%2F%2Fwww.tumblr.com%2Fbuttons&shareSource=tumblr_share_button`}
                    target="_blank"
                    rel="noopener noreferrer"
                >
                    <FontAwesomeIcon icon={faTumblr}/>
                    {tumblrBtn}
                </a>
                <a
                    className="button"
                    id="facebook-quote"
                    href={`https://www.facebook.com/sharer/sharer.php?u=https%3A%2F%2Fwww.facebook.com%2F&quote=${currentQuote.content} - ${currentQuote.author}`}
                    target="_blank"
                    rel="noopener noreferrer"
                >
                    <FontAwesomeIcon icon={faFacebook}/>
                    {facebookBtn}
                </a>
            </div>
            <div className="search-container">
                <input
                    type="text"
                    className="search-input"
                    id="search"
                    placeholder={searchPlaceholder}
                    value={searchValue}
                    onChange={this.handleSearchChange}
                />
            </div>
            <div className="font-size-container">
                <label htmlFor="font-size">Font size</label>
                <input
                    type="range"
                    className="font-size-input"
                    id="font-size"
                    min="10"
                        max="30"
                        value={this.state.fontSize}
                        onChange={this.handleFontSizeChange}
                    />
                </div>
                <div className="bg-color-container">
                    <label htmlFor="bg-color">Background color</label>
                    <input
                        type="color"
                        className="bg-color-input"
                        id="bg-color"
                        value={this.state.bgColor}
                        onChange={this.handleBgColorChange}
                    />
                </div>
                <div className="favorite-quotes-container">
                    <h2>Favorite quotes</h2>
                    <ul>
                        {favoriteQuotes.map((quote, index) => (
                            <li key={index}>{quote.content}</li>
                        ))}
                    </ul>
                </div>
            </>
        )}
        </div>
        );
    }
}

export default QuoteBox;
