import React from "react";
import languages from "../languages";
import {Box, Button, TextField} from "@mui/material";

// Define a QuoteBox component
function QuoteBox(props) {
  // Get props from parent component
  const {
    quotes,
    authors,
    isLoading,
    language,
    favorites,
    searchQuery,
    backgroundColor,
    fontSize,
    getNewQuote,
    handleLanguageChange,
    handleFavorite,
    handleSearch,
    handleBackgroundColorChange,
    handleFontSizeChange
  } = props;

  // Get the current quote and author based on language and search query
  const currentQuote = quotes[searchQuery]
  const currentAuthor = authors[searchQuery]

  // Return JSX for rendering the quote box
  return (
      <Box
          sx={{
            width: "80%",
            margin: "auto",
            padding: "2rem",
            border: "1px solid black",
            borderRadius: "1rem",
            backgroundColor: backgroundColor, // set background color from state
          }}
          id="quote-box"
          className="quote-box"
      >
        {/* Display loading message if fetching data */}
        {isLoading && <p>Loading...</p>}

        {/* Display quote and author if data is available */}
        {!isLoading && (
            <>
              <p id="text" style={{fontSize: fontSize}}> {/* set font size from state */}
                "{currentQuote}"
              </p>
              <p id="author">- {currentAuthor}</p>
              {/* Display buttons for new quote, favorite and tweet */}
              <Box sx={{display: "flex", justifyContent: "space-between"}}>
                <Button variant="contained" color="primary" onClick={getNewQuote} id="new-quote">
                  New Quote
                </Button>
                <Button variant="outlined" color="secondary" onClick={handleFavorite} id="favorite">
                  {favorites.includes(currentQuote) ? "Unfavorite" : "Favorite"}
                </Button>
                <Button variant="contained" color="info"
                        href={`https://twitter.com/intent/tweet?text=${currentQuote} - ${currentAuthor}`}
                        target="_blank" rel="noopener noreferrer" id="tweet-quote">
                  Tweet Quote
                </Button>
              </Box>
              {/* Display language selector and search input */}
              <Box sx={{display: "flex", alignItems: "center", marginTop: "1rem"}}>
                <label htmlFor="language">Language:</label>
                <select name="language" id="language" value={language} onChange={handleLanguageChange}>
                  {Object.keys(quotes).map((lang) => (
                      <option key={lang} value={lang}>
                        {languages[lang]}
                      </option>
                  ))}
                </select>
                <label htmlFor="search">Search by author:</label>
                <TextField name="search" id="search" value={searchQuery} onChange={handleSearch}/>
              </Box>
              {/* Display background color and font size inputs */}
              <Box sx={{display: "flex", alignItems: "center", marginTop: "1rem"}}>
                <label htmlFor="background-color">Background Color:</label>
                <input type="color" name="background-color" id="background-color" value={backgroundColor}
                       onChange={handleBackgroundColorChange}/>


                <label htmlFor="font-size">Font Size:</label>
                <input type="number" name="font-size" id="font-size" value={fontSize.slice(0, -2)}
                       onChange={handleFontSizeChange}/> px
              </Box>
            </>
        )}
      </Box>
  );
}

// Export the component
export default QuoteBox;
